package io.muhwyndhamhp.catnip.base

import androidx.hilt.lifecycle.ViewModelInject
import androidx.lifecycle.*
import io.muhwyndhamhp.catnip.model.Cat
import io.muhwyndhamhp.catnip.model.User
import kotlinx.coroutines.launch

class BaseViewModel @ViewModelInject constructor(private val baseRepository: BaseRepository) :
    ViewModel() {

    val user: LiveData<User?> = liveData {
        val userMediator = MediatorLiveData<User>()
        emitSource(userMediator)
        userMediator.addSource(baseRepository.getUser()) {
            if (it.isNullOrEmpty()) userMediator.postValue(null)
            else userMediator.postValue(it[0])
        }
    }

    val catList: LiveData<List<Cat>> = liveData { emitSource(baseRepository.getCats()) }

    fun prepopulateDatabase() = viewModelScope.launch {
        baseRepository.prepopulateDatabase()
    }

    fun insertUser(email: String, password: String) {
        baseRepository.insertUser(
            User(
                name = "Cat Nipper User",
                email = email,
                phone = "+62-882-2866-4820",
                password = password,
                profileImage = "data:image/jpeg;base64,"
            )
        )
    }
}